var React       = require("react"),
    TaghubInput = require("../swissArmyInput"),
    mock        = require("../test/mock"),
    Main        = React.createClass({
        render : function() {
            var fields  = mock.fields.map(function(field) {
                    return(
                        TaghubInput({
                            id          : field.id,
                            type        : field.type,
                            className   : field.className,
                            placeholder : field.placeholder,
                            value       : field.value,
                            maxLength   : field.maxLength,
                            labelText   : field.labelText,
                            key         : field.id
                        })
                    )
                });

            return (
                React.DOM.div(
                    {},
                    [
                        fields
                    ]
                )
            )
        }
    });

React.renderComponent(Main({}), document.body);
