var gulp            = require("gulp"),
    gutil           = require("gulp-util"),
    uglify          = require("gulp-uglify"),
    rename          = require("gulp-rename"),
    size            = require("gulp-size"),
    webpack         = require("webpack"),
    webpack_config  = require("./webpack.config.js"),
    pkg             = require("../package.json"),
    _config         = Object.create(webpack_config);

_config.debug   = true;
var _compiler   = webpack(_config);

gulp.task(
    "webpack",
    function(callback) {
        _compiler.run(function(
            err,
            stats
            ) {
                if (err) throw new gutil.PluginError(
                    "webpack",
                    err
                );
                gutil.log(
                    "[webpack]",
                    stats.toString(
                        {
                            colors  : true
                        }
                    )
                );
                if (typeof callback == "function") callback()
            }
        )
    }
);
gulp.task(
    "build",
    ["webpack"],
    function() {
        return gulp.src("dist/bundle.js")
            .pipe(
                size(
                    {
                        showFiles   :true
                    }
                )
            )
            .pipe(
                uglify()
            )
            .pipe(
                rename(pkg.name+".min.js")
            )
            .pipe(
                size(
                    {
                        showFiles   :true
                    }
                )
            )
            .pipe(
                gulp.dest("./dist/"+pkg.version)
            )
    }
);
module.exports  = gulp;
