var React               = require("react"),
    taghubInputLabel    = React.createClass(
        {
            render              : function() {
                return (
                    React.DOM.label(
                        {
                            className   : "swissArmyInput",
                            htmlFor     : this.props.id
                        },
                        this.props.labelText
                    )
                )
            }
        }
    );

module.exports  = taghubInputLabel;
